import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import Filters from './Filters'; // Import Filters component
import '../home.css';

const Home = () => {
    const [properties, setProperties] = useState([]);
    const [filteredProperties, setFilteredProperties] = useState([]);

    useEffect(() => {
        const fetchProperties = async () => {
            try {
                const properties = await getAllProperties();
                setProperties(properties);
                setFilteredProperties(properties);
            } catch (error) {
                console.error('Error fetching properties:', error);
            }
        };
        fetchProperties();
    }, []);

    const handleSearch = (term) => {
        const filtered = properties.filter(property =>
            property.title.toLowerCase().includes(term.toLowerCase()) ||
            property.location.toLowerCase().includes(term.toLowerCase())
        );
        setFilteredProperties(filtered);
    };

    return (
        <div className="home-container">
            <Filters onSearch={handleSearch} />
            <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false} className="map-container">
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                {filteredProperties.map(property => (
                    <Marker key={property.id} position={[property.latitude, property.longitude]}>
                        <Popup>
                            <div className="popup-content">
                                <h2>{property.title}</h2>
                                <p>{property.description}</p>
                                <p><strong>Price:</strong> ${property.price}</p>
                            </div>
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>
            <div className="property-list">
                {filteredProperties.map(property => (
                    <div key={property.id} className="property-card">
                        <img src={`http://localhost:5000/uploads/${property.image}`} alt="Property" className="property-image" />
                        <div className="property-details">
                            <h2>{property.title}</h2>
                            <p>{property.description}</p>
                            <p><strong>Location:</strong> {property.location}</p>
                            <p><strong>Price:</strong> ${property.price}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
