// components/Profile.js

import React, { useState, useEffect } from 'react';
import { getUserProfile } from '../services/authService';
import { getUserFavorites } from '../services/propertyService';
import { useNavigate } from 'react-router-dom';
import '../profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [favorites, setFavorites] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProfileData = async () => {
            const token = sessionStorage.getItem('token');
            if (!token) {
                console.error('No token found');
                return;
            }

            try {
                const userData = await getUserProfile(token);
                setUser(userData);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching user data:', error);
                setLoading(false);
            }
        };

        const fetchUserFavorites = async () => {
            const token = sessionStorage.getItem('token');
            if (!token) {
                console.error('No token found');
                return;
            }

            try {
                const favoriteData = await getUserFavorites(token);
                setFavorites(favoriteData);
            } catch (error) {
                console.error('Error fetching user favorites:', error);
            }
        };

        fetchProfileData();
        fetchUserFavorites();
    }, []);

    const handleEdit = (propertyId) => {
        navigate(`/editproperty/${propertyId}`);
    };

    const handleDelete = async (propertyId) => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            console.error('No token found');
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/properties/${propertyId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.ok) {
                // Remove the deleted property from the state
                setFavorites(favorites.filter(favorite => favorite.property.id !== propertyId));
            } else {
                const errorData = await response.json();
                console.error('Error deleting property:', errorData);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (loading) {
        return <p>Loading user profile...</p>;
    }

    return (
        <div className="profile-container">
            <header className="profile-header">
                <h1>My Profile</h1>
            </header>
            {user && (
                <div className="user-details-card">
                    <h2>{user.username}</h2>
                    <p>Email: {user.email}</p>
                    <p>Phone: {user.phone}</p>
                </div>
            )}
            {favorites.length > 0 && (
                <div className="favorites-section">
                    <h2>Favorite Properties</h2>
                    <div className="property-list">
                        {favorites.map((favorite) => (
                            <div key={favorite.property.id} className="property-card">
                                <div className="property-details">
                                    <h2>{favorite.property.title}</h2>
                                    <p>{favorite.property.description}</p>
                                    <p>{favorite.property.location}</p>
                                    <p>${favorite.property.price}</p>
                                    {favorite.property.image && <img src={`http://localhost:5000/uploads/${favorite.property.image}`} alt={favorite.property.title} className="property-image" />}
                                </div>
                                <div className="property-buttons">
                                    <button onClick={() => handleEdit(favorite.property.id)}>Edit</button>
                                    <button className="delete" onClick={() => handleDelete(favorite.property.id)}>Delete</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Profile;
